import React from 'react';
import { Button, Form, FormGroup, Input, Label } from 'reactstrap';
import { USERS_API_URL } from '../../constants';
class RegistrationForm extends React.Component {
    state = {
        id: 0,
        name: '',
        sports: '',
        breed: '',
        weight: ''
    }
    componentDidMount() {
        if (this.props.user) {
            const { id, name, sports, breed, weight } = this.props.user
            this.setState({ id, name, sports, breed, weight});
        }
    }
    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }
    submitNew = e => {
        e.preventDefault();
        fetch(`${USERS_API_URL}`, {
            method: 'post',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name: this.state.name,
                sports: this.state.sports,
                breed: this.state.breed,
                weight: this.state.weight
            })
        })
            .then(res => res.json())
            .then(user => {
                this.props.addUserToState(user);
                this.props.toggle();
            })
            .catch(err => console.log(err));
    }
    submitEdit = e => {
        e.preventDefault();
        fetch(`${USERS_API_URL}/${this.state.id}`, {
            method: 'put',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                name: this.state.name,
                sports: this.state.sports,
                breed: this.state.breed,
                weight: this.state.weight
            })
        })
            .then(() => {
                this.props.toggle();
                this.props.updateUserIntoState(this.state.id);
            })
            .catch(err => console.log(err));
    }
    render() {
        return <Form onSubmit={this.props.user ? this.submitEdit : this.submitNew}>
            <FormGroup>
                <Label for="name">Name:</Label>
                <Input type="text" name="name" onChange={this.onChange} value={this.state.name === '' ? '' : this.state.name} />
            </FormGroup>
            <FormGroup>
                <Label for="document">Sports:</Label>
                <Input type="text" name="sports" onChange={this.onChange} value={this.state.sports === null ? '' : this.state.sports} />
            </FormGroup>
            <FormGroup>
                <Label for="email">Breed:</Label>
                <Input type="email" name="breed" onChange={this.onChange} value={this.state.breed === null ? '' : this.state.breed} />
            </FormGroup>
            <FormGroup>
                <Label for="phone">Phone:</Label>
                <Input type="text" name="weight" onChange={this.onChange} value={this.state.weight === null ? '' : this.state.weight} />
            </FormGroup>
            <Button>Send</Button>
        </Form>;
    }
}
export default RegistrationForm;
